import React, { useReducer } from 'react';
import { makeStyles } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import Button from '@material-ui/core/Button';
import styles from './styles'
import DialogTitle from '@material-ui/core/DialogTitle';
import DialogContent from '@material-ui/core/DialogContent';
import DialogActions from '@material-ui/core/DialogActions';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';

type Props = {
  openDialog: boolean;
  setOpenDialog: Function;
}

const useStyles = makeStyles(styles);

const ActivityPopup: React.FC<Props> = (props) => {
  const classes = useStyles()

  const [formInput, setFormInput] = useReducer(
    (state, newState) => ({ ...state, ...newState }),
    {
      activityName: "",
    }
  );

  const handleClose = () => {
    props.setOpenDialog(false);
  };

  const resetInput = () => {
    setFormInput({
      'activityName': null
    });
  }

  const handleSubmit = evt => {
    evt.preventDefault();

    console.log('submitted', formInput)

    // if (formInput?.comment) {
    //   setCommentsValue({
    //     text: formInput?.comment,
    //     stateLocation,
    //     photoId
    //   })

    //   resetInput()
    // }
  };


  const handleInput = evt => {
    const name = evt.target.name;
    const newValue = evt.target.value;
    setFormInput({ [name]: newValue });
  };

  return (
    <Dialog className={classes.dialog} open={props.openDialog} onClose={handleClose} aria-labelledby="form-dialog-title">
      <DialogTitle id="form-dialog-title" className={classes.dialogTitle}>
        Tambah Activity

        <IconButton aria-label="close" className={classes.closeButton} onClick={handleClose}>
          <CloseIcon />
        </IconButton>
      </DialogTitle>

      <DialogContent dividers className={classes.dialogContent}>
        <form onSubmit={handleSubmit}>
          <div className={classes.formGroup}>
            <label htmlFor="activityName">Nama Activity</label>
            <TextField
              rows={4}
              name="activityName"
              placeholder="Tambahkan nama activity"
              value={formInput.activityName || ""}
              variant="outlined"
              className={classes.activityNameInput}
              onChange={handleInput}
              autoComplete="off"
              required
            />
          </div>
        </form>
      </DialogContent>

      <DialogActions>
        <Button type="submit" onClick={handleSubmit} variant="contained" color="primary">
          Simpan
        </Button>
      </DialogActions>
    </Dialog>
  );
}

export default ActivityPopup
