import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { useRouter } from "next/router"
import styles from './styles'
import ActivityHeader from '../../../components/activity-header';
import ActivityPopup from '../../activityPopup';

const useStyles = makeStyles(styles)

const DetailActivity = () => {
  const cs = useStyles()
  const router = useRouter()

  const [addActivityDialog, setAddActivityDialog] = useState(false);
  const [editActivityDialog, setEditActivityDialog] = useState(false);

  const fetchData = () => {
    // const url = ACTIVITY_GROUPS + '?email=notfound@gmail.com'

    const onFetch = async () => {
      //   try {
      //     const responses = await get(url)
      //     setData({
      //       name: '@@FetchActivity',
      //       items: responses?.data
      //     })
      //     setLoading(false)
      //   } catch (error) {
      //     setLoading(true)
      //   }
    }

    onFetch()
  }

  useEffect(fetchData, []);

  useEffect(() => {
    console.log('router', router)
  }, [router])

  const renderChildDialog = () => {
    if (addActivityDialog) return (
      <ActivityPopup
        initialValues={{ activityName: '' }}
        openDialog={addActivityDialog}
        setOpenDialog={setAddActivityDialog}
        onRefetch={fetchData}
      />
    )

    return (
      <ActivityPopup
        initialValues={{ activityName: "Update" }}
        openDialog={editActivityDialog}
        setOpenDialog={setEditActivityDialog}
        onRefetch={fetchData}
      />
    )
  }

  return (
    <div role="detailActivity">
      <ActivityHeader
        title={router?.query?.slug}
        onClick={() => setAddActivityDialog(true)}
        onUpdate={() => setEditActivityDialog(true)}
        children={renderChildDialog()}
      />
    </div>
  )
}

export default DetailActivity
