import {
  Theme,
  createStyles
} from "@material-ui/core/styles";

const styles = (theme: Theme) => createStyles({
  activityContainer: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  activityTitle: {
    fontWeight: 'bold'
  },
  btnAdd: {
    textTransform: 'capitalize',
  },
  dialog: {
    "& .MuiPaper-root": {
      width: 550
    }
  },
  activityNameInput: {
    width: '100%',
    height: 42,
    border: 'solid 1px #e5e5e5',
    borderRadius: 7,
    padding: '4px 8px',
  },
  formGroup: {
    "& label": {
      display: 'block',
      margin: '10px 0',
      textTransform: 'uppercase',
      color: '#000',
      fontWeight: 'bold',
    }
  },
  dialogContent: {
    paddingBottom: 30,
  }
})

export default styles
