import Axios from 'axios'
import { useEffect, useState } from 'react'
import { get } from '../api/API'
import { ACTIVITY_GROUPS } from '../constant/api'
import { useData } from '../store/configureStore'

export const useRefetch = (url = ACTIVITY_GROUPS + '?email=notfound@gmail.com') => {
  const [doRefetch, setDoRefetch] = useState(false)
  const [, setLoading, , setData] = useData()

  const onRefetch = async () => {
    try {
      const responses = await get(url)
      setData({
        name: '@@FetchActivity',
        items: responses?.data
      })
      setLoading(false)
    } catch (error) {
      setLoading(true)
    }
  }

  useEffect(() => {
    if (doRefetch) onRefetch()
  }, [doRefetch])

  return {
    setDoRefetch,
  }
}
