import React, { useCallback } from 'react';
import Header from './header';
import { makeStyles } from '@material-ui/core';
import styles from './styles'
import Container from '@material-ui/core/Container';
import { useData } from '../store/configureStore';
import { useRouter } from 'next/router';
import ActivityHeader from '../components/activity-header';
import { get } from '../api/API';
import { ACTIVITY_GROUPS, TODO_ITEMS } from '../constant/api';

const useStyles = makeStyles(styles);

const Layout: React.FC = (props) => {
  const classes = useStyles()
  const { route, query } = useRouter()

  const [, setLoading, , setData] = useData()

  const refetchData = useCallback(() => {
    const urls = {
      '/': ACTIVITY_GROUPS + '?email=notfound@gmail.com',
      'activity':  TODO_ITEMS + `/${query}`
    }

    const onRefetch = async () => {
      try {
        const url = urls[route]
        const responses = await get(url)
        setData({
          name: '@@FetchActivity',
          items: responses?.data
        })
        setLoading(false)
      } catch (error) {
        setLoading(true)
      }
    }
    onRefetch()
  }, [route])

  return (
    <React.Fragment>
      <Header />
        <Container maxWidth="md" className={classes.layout}>
          <ActivityHeader title="Activity" onRefetch={refetchData} />
          {props.children}
        </Container>
    </React.Fragment>
  );
};

export default Layout;
