import React, { useCallback } from 'react';
import Header from './header';
import { makeStyles } from '@material-ui/core';
import styles from './styles'
import Container from '@material-ui/core/Container';
import { AllContextProvider, useData } from '../store/configureStore';
import { useRouter } from 'next/router';
import ActivityHeader from '../components/activity-header';
import { get } from '../api/API';
import { ACTIVITY_GROUPS } from '../constant/api';

const useStyles = makeStyles(styles);

const Layout: React.FC = (props) => {
  const classes = useStyles()
  const router = useRouter()

  const [loading, setLoading, data, setData] = useData()

  const fetchData = useCallback(() => {
    const url = ACTIVITY_GROUPS + '?email=notfound@gmail.com'

    const onRefetch = async () => {
      try {
        const responses = await get(url)
        setData({
          name: '@@FetchActivity',
          items: responses?.data
        })
        setLoading(false)
      } catch (error) {
        setLoading(true)
      }
    }
    onRefetch()
  }, [])

  return (
    <React.Fragment>
      <Header />
      <AllContextProvider>
        <Container maxWidth="md" className={classes.layout}>
          <ActivityHeader title="Activity" onRefetch={fetchData} />
          {props.children}
        </Container>
      </AllContextProvider>
    </React.Fragment>
  );
};

export default Layout;
