import { useState, useRef, useEffect, useCallback } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import styles from './styles'
import React from 'react';
import Grid from '@material-ui/core/Grid';
import IconButton from '@material-ui/core/IconButton';
import DeleteOutlineIcon from '@material-ui/icons/DeleteOutline';
import { mappingColorPriority } from '../../../../utils/utils';
import { useRouter } from 'next/router';
import { ACTIVITY_GROUPS } from '../../../../constant/api';
import { get } from '../../../../api/API';
import EmptyItems from '../../../../components/empty-items';

const useStyles = makeStyles(styles)

const CardItems = () => {
  const cs = useStyles()
  const { query } = useRouter()

  const checkedRef = useRef({})
  const [items, setItems] = useState([])
  const [itemEmpty, setItemEmpty] = useState(false)
  const [checkedState, setCheckedState] = useState({});

  const fetchData = useCallback(() => {
    const url = ACTIVITY_GROUPS + `/${query?.id}`

    const onFetch = async () => {
      try {
        const responses = await get(url)

        if (responses?.todo_items.length) {
          setItems(responses?.todo_items)
        } else {
          setItemEmpty(true)
        }
      } catch (error) {
        console.error(error)
      }
    }

    onFetch()
  }, [])

  useEffect(fetchData, [])

  // Create initial state for checkBox
  useEffect(() => {
    Object.keys(items).map(v => {
      checkedRef.current[`checked_${items[v].id}`] = items[v]['is_active'] === '0'
    })

    setCheckedState({ ...checkedRef.current })
  }, [])

  const handleCheckChange = (event) => {
    setCheckedState({ ...checkedState, [event.target.name]: event.target.checked });
  };

  const handleDelete = () => {

  }

  const renderItems = () => {
    if (itemEmpty) return <EmptyItems path="../todo-empty-state.png" />

    return items.map((v, idx) => {
      const key = `checked_${v.id}`

      return (
        <Grid item sm={12} key={key}>
          <Card className={cs.root}>
            <span
              role="priority"
              className={cs.prior}
              style={{ background: mappingColorPriority(v.priority) }}
            />

            <FormControlLabel
              className={cs.formLabel}
              key={idx}
              control={
                // Issues: Component is changing the controlled value state of Select to be uncontrolled.
                // Solve by: https://stackoverflow.com/questions/69259429/material-ui-a-component-is-changing-the-uncontrolled-checked-state-of-switchbas
                <Checkbox
                  color="primary"
                  checked={!!checkedState[key]}
                  onChange={handleCheckChange}
                  name={key}
                />
              }
              label={v.name}
            />

            <IconButton onClick={handleDelete} aria-label="delete">
              <DeleteOutlineIcon />
            </IconButton>
          </Card>
        </Grid>
      )
    })
  }

  return <React.Fragment children={renderItems()} />
}

export default CardItems
