import React, { useState, useCallback, useEffect } from "react"
import ActivityCard from "../components/activity-card"
import ActivityHeader from "../components/activity-header"
import Loading from "../components/loading"
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import EmptyItems from "../components/empty-items";
import { get, remove } from '../api/API'
import { ACTIVITY_GROUPS } from '../constant/api'
import { parseDate } from "../utils/utils";
import AlertNotification from "../components/alert-notification";
import ConfirmPopup from '../components/confirm-popup'

const useStyles = makeStyles(({}))

const Activity = () => {
  const classes = useStyles()
  const [loading, setLoading] = useState(true)
  const [visibleAlert, setVisibleAlert] = useState(false)
  const [data, setData] = useState([])

  const fetchData = () => {
    const url = ACTIVITY_GROUPS + '?email=notfound@gmail.com'

    const onFetch = async () => {
      try {
        const responses = await get(url)
        setData(responses?.data)
        setLoading(false)
       } catch (error) {
        setLoading(true)
       }
    }

    onFetch()
  }

  useEffect(fetchData, []);

  const handleDelete = useCallback((id: string) => {
    const url = ACTIVITY_GROUPS + `/${id}`
    setVisibleAlert(true)

    const onDelete = async () => {
      setLoading(true)

      try {
        await remove(url)
        fetchData()
       } catch (error) {
        console.error(error)
       }
    }

    onDelete()
  }, [fetchData])

  const renderActivityItems = useCallback(() => {
    if (loading) return <Loading />

    if (!data.length) return (
      <EmptyItems type="activity" />
    )

    return data.map((item, index) => 
      <Grid item xs={12} sm={3} key={`item-${item.id}-${index}`}>
        <ActivityCard
          title={item.title}
          date={parseDate(item.created_at)}
          onDelete={() => handleDelete(item.id)}
        />
      </Grid>
    )
  }, [data, loading])

  console.log('re-rendered times')

  return (
    <div role="activity">
      <ActivityHeader onRefetch={fetchData} />

      <Grid container spacing={3} alignItems="stretch">
        {renderActivityItems()}
      </Grid>

      <ConfirmPopup />

      <AlertNotification 
        visibleAlert={visibleAlert} 
        setVisibleAlert={setVisibleAlert}
        title="Activity berhasil dihapus" 
      />
    </div>
  )
}

export default Activity
