import React, { useCallback } from 'react';
import Header from './header';
import { makeStyles } from '@material-ui/core';
import styles from './styles'
import Container from '@material-ui/core/Container';
import { AllContextProvider, useData } from '../store/configureStore';
import { useRouter } from 'next/router';
import ActivityHeader from '../components/activity-header';
import { useRefetch } from '../hooks/useRefetch';
import { ACTIVITY_GROUPS } from '../constant/api';

const useStyles = makeStyles(styles);

const Layout: React.FC = (props) => {
  const classes = useStyles()
  const router = useRouter()
  const { setDoRefetch } = useRefetch(ACTIVITY_GROUPS + '?email=notfound@gmail.com')

  return (
    <React.Fragment>
      <Header />
      <AllContextProvider>
        <Container maxWidth="md" className={classes.layout}>
          <ActivityHeader title="Activity" onRefetch={() => setDoRefetch(true)} />
          {props.children}
        </Container>
      </AllContextProvider>
    </React.Fragment>
  );
};

export default Layout;
