import {
  Theme,
  createStyles
} from "@material-ui/core/styles";

const styles = (theme: Theme) => createStyles({
  activityContainer: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingBottom: 40,
  },
  activityTitle: {
    fontWeight: 'bold',
    textTransform: 'capitalize'
  },
  btnAdd: {
    textTransform: 'capitalize',
  },
  dialog: {
    "& .MuiPaper-root": {
      width: 550
    }
  },
  activityNameInput: {
    width: '100%',
    height: 42,
    border: 'solid 1px #e5e5e5',
    borderRadius: 7,
    padding: '4px 8px',
  },
  formGroup: {
    position: 'relative',

    "& label": {
      display: 'block',
      margin: '10px 0',
      textTransform: 'uppercase',
      color: '#000',
      fontSize: 13,
      fontWeight: 'bold',
    }
  },
  dialogTitle: {
    color: '#000',

    "& h2": {
      fontWeight: 'bold',
    }
  },
  dialogContent: {
    paddingBottom: 30,
  },
  closeButton: {
    position: 'absolute',
    right: theme.spacing(1),
    top: theme.spacing(1),
    color: theme.palette.grey[500],
  },
  error: {
    fontSize: 13,
    color: 'red',
    position: 'absolute',
    bottom: '9px',
    left: 0,
  },
  formikErrors: {
    borderColor: 'red'
  },
  asideLeft: {
    display: 'flex',
    alignItems: 'center'
  },
  btnPrev: {
    width: 10,
    color: "#111",
    padding: '0',
    minWidth: '10px',
    marginRight: 10,
}
})

export default styles
