import React, { useCallback, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import Button from '@material-ui/core/Button';
import styles from './styles'
import DialogTitle from '@material-ui/core/DialogTitle';
import DialogContent from '@material-ui/core/DialogContent';
import DialogActions from '@material-ui/core/DialogActions';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import * as yup from 'yup';
import { useFormik } from 'formik';
import { ACTIVITY_GROUPS } from '../../constant/api';
import { post } from '../../api/API'
import CircularProgress from '@material-ui/core/CircularProgress';

type Props = {
  openDialog: boolean;
  setOpenDialog: Function;
  onRefetch: Function
}

const validationSchema = yup.object({
  activityName: yup.string().required(),
});

const useStyles = makeStyles(styles);

const ActivityPopup: React.FC<Props> = (props) => {
  const classes = useStyles()
  const [loading, setLoading] = useState(false);

  const formik = useFormik({
    initialValues: {
      activityName: '',
    },
    validationSchema,
    onSubmit: (values) => {
      const { activityName } = values

      console.log('submitted', activityName)
      handlePostData(activityName)
    },
  })

  const handlePostData = useCallback((title: string) => {
    const url = ACTIVITY_GROUPS

    const manual = {
      email: "notfound@gmail.com",
      comment: "Comment confirmed!"
    }

    const onPost = async () => {
      try {
        await post(url, {
          title,
          ...manual,
        })
        setLoading(false)
        formik.resetForm()
      } 
      catch (error) {
        setLoading(true)
      } 
      finally {
        handleClose()
        props.onRefetch()
      }
    }

    onPost()
  }, [props.onRefetch])

  const handleClose = () => {
    props.setOpenDialog(false);
  };

  return (
    <Dialog className={classes.dialog} open={props.openDialog} onClose={handleClose} aria-labelledby="form-dialog-title">
      <DialogTitle id="form-dialog-title" className={classes.dialogTitle}>
        Tambah Activity

        <IconButton aria-label="close" className={classes.closeButton} onClick={handleClose}>
          <CloseIcon />
        </IconButton>
      </DialogTitle>

      <DialogContent dividers className={classes.dialogContent}>
        <form onSubmit={formik.handleSubmit}>
          <div className={classes.formGroup}>
            <label htmlFor="activityName">Nama Activity</label>
            <TextField
              rows={4}
              name="activityName"
              placeholder="Tambahkan nama activity"
              value={formik.values.activityName}
              variant="outlined"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              autoFocus
              className={
                `${formik.errors.activityName && formik.touched.activityName ? classes.formikErrors : null} ${classes.activityNameInput}`
              }
              autoComplete="off"
              required
            />
          </div>
        </form>
      </DialogContent>

      <DialogActions>
        <Button type="submit" onClick={() => formik.handleSubmit()} variant="contained" color="primary">
          {loading && <CircularProgress color="secondary" />} Simpan
        </Button>
      </DialogActions>
    </Dialog>
  );
}

export default ActivityPopup
