import {
  Theme,
  createStyles
} from "@material-ui/core/styles";

const styles = (theme: Theme) => createStyles({
  alertRoot: {
    background: '#fff',
    padding: 20,
    color: "#111",
    borderRadius: 12,
    height: 58,
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
    display: 'flex',
    alignItems: 'center',
    // position: 'absolute',
    // right: 0,
    // bottom: 0,
  },
  title: {
    display: 'inline-block',
    paddingLeft: 10,
  }
})

export default styles
