import Dialog from '@material-ui/core/Dialog';
import Button from '@material-ui/core/Button';
import DialogTitle from '@material-ui/core/DialogTitle';
import DialogContent from '@material-ui/core/DialogContent';
import DialogActions from '@material-ui/core/DialogActions';
import { makeStyles } from '@material-ui/core/styles';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import TextField from '@material-ui/core/TextField';
import styles from './styles'
import { useFormik } from 'formik';
import * as yup from 'yup';

const useStyles = makeStyles(styles);

const validationSchema = yup.object({
  activityName: yup.string().required(),
});

type Props = {
  openDialog: boolean;
  title: string;
  setOpenDialog: Function;
  onRefetch: Function
}

const TodoPopup: React.FC<Props> = (props) => {
  const classes = useStyles()

  const formik = useFormik({
    initialValues: {
      activityName: ''
    },
    validationSchema,
    onSubmit: (values) => {
      const { activityName } = values!!

      // handlePostData(activityName)
    },
  })

  const handleClose = () => {
    props.setOpenDialog(false);
  };

  return (
    <Dialog className={classes.dialog} open={props.openDialog} onClose={handleClose} aria-labelledby="form-dialog-title">
      <DialogTitle id="form-dialog-title" className={classes.dialogTitle}>
        "TITLE"

        <IconButton aria-label="close" className={classes.closeButton} onClick={handleClose}>
          <CloseIcon />
        </IconButton>
      </DialogTitle>

      <DialogContent dividers className={classes.dialogContent}>
        <form onSubmit={formik.handleSubmit}>
          <div className={classes.formGroup}>
            <label htmlFor="activityName">Nama Activity</label>
            <TextField
              rows={4}
              name="activityName"
              placeholder="Tambahkan nama activity"
              value={formik.values.activityName}
              variant="outlined"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              autoFocus
              className={
                `${formik.errors.activityName && formik.touched.activityName ? classes.formikErrors : null} ${classes.activityNameInput}`
              }
              autoComplete="off"
              required
            />
          </div>
        </form>
      </DialogContent>

      <DialogActions>
        <Button type="submit" onClick={() => formik.handleSubmit()} variant="contained" color="primary">
           Simpan
        </Button>
      </DialogActions>
    </Dialog>
  );
}

export default TodoPopup
