import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { useRouter } from "next/router"
import styles from './styles'
import ActivityHeader from '../../../components/activity-header';
import ActivityPopup from '../../activityPopup';
import { ACTIVITY_GROUPS } from '../../../constant/api';
import { get } from '../../../api/API';

const useStyles = makeStyles(styles)

const DetailActivity = () => {
  const cs = useStyles()
  const router = useRouter()

  const [data, setData] = useState({
    title: '',
  })
  const [loading, setLoading] = useState(false);
  const [addActivityDialog, setAddActivityDialog] = useState(false);
  const [editActivityDialog, setEditActivityDialog] = useState(false);

  const fetchDataById = () => {
    const url = ACTIVITY_GROUPS + `/${router.query?.id}`

    const onFetch = async () => {
        try {
          const responses = await get(url)
          setData({
            title: responses?.title
          })
          setLoading(false)
        } catch (error) {
          console.error(error)
          setLoading(true)
        }
    }

    onFetch()
  }

  useEffect(fetchDataById, []);

  const renderChildDialog = () => {
    if (addActivityDialog && !editActivityDialog) return (
      <ActivityPopup
        type="add"
        title="Tambah Activity"
        initialValues={{ activityName: '' }}
        openDialog={addActivityDialog}
        setOpenDialog={setAddActivityDialog}
        onRefetch={fetchDataById}
      />
    )

    if (editActivityDialog && !addActivityDialog) return (
      <ActivityPopup
        type="edit"
        title="Edit Activity"
        initialValues={{ activityName: data?.title }}
        openDialog={editActivityDialog}
        setOpenDialog={setEditActivityDialog}
        onRefetch={fetchDataById}
      />
    )

    return <React.Fragment />
  }

  return (
    <div role="detailActivity">
      <ActivityHeader
        title={loading ? "Loading..." : data?.title}
        onClick={() => setAddActivityDialog(true)}
        onUpdate={() => setEditActivityDialog(true)}
        children={renderChildDialog()}
      />
    </div>
  )
}

export default DetailActivity
