import React, { useReducer } from 'react';
import { makeStyles } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import CloseIcon from '@material-ui/icons/Close';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import ListItemText from '@material-ui/core/ListItemText';
import ListItem from '@material-ui/core/ListItem';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import styles from './styles'
import Container from '@material-ui/core/Container';
import DialogTitle from '@material-ui/core/DialogTitle';
import DialogContent from '@material-ui/core/DialogContent';
import DialogActions from '@material-ui/core/DialogActions';

type Props = {
  openDialog: boolean;
  setOpenDialog: Function;
}

const useStyles = makeStyles(styles);

const ActivityPopup: React.FC<Props> = (props) => {
  const classes = useStyles()
  // const { stateLocation, photoId } = props

  // const [usersData, , setCommentsValue] = useUsersData()
  // const comments = usersData.users.comments[props.photoId]

  const [formInput, setFormInput] = useReducer(
    (state, newState) => ({ ...state, ...newState }),
    {
      comment: "",
    }
  );

  const handleClose = () => {
    props.setOpenDialog(false);
  };

  const resetInput = () => {
    setFormInput({
      'comment': null
    });
  }

  const handleSubmit = evt => {
    evt.preventDefault();

    console.log('submitted')

    // if (formInput?.comment) {
    //   setCommentsValue({
    //     text: formInput?.comment,
    //     stateLocation,
    //     photoId
    //   })

    //   resetInput()
    // }
  };


  const handleInput = evt => {
    const name = evt.target.name;
    const newValue = evt.target.value;
    setFormInput({ [name]: newValue });
  };

  return (
    <Dialog className={classes.dialog} open={props.openDialog} onClose={handleClose} aria-labelledby="form-dialog-title">
      <DialogTitle id="form-dialog-title">Subscribe</DialogTitle>

      <DialogContent dividers>
        {/* <List className={classes.rootList}>
          {comments && comments.contents.map(({ text }, index) => (
            <React.Fragment key={`list-${index}`}>
              <Divider />
              <ListItem>
                <ListItemText secondary={text} />
              </ListItem>
              <Divider />
            </React.Fragment>
          ))}
        </List> */}

        <form onSubmit={handleSubmit}>
          <TextField
            label="Add your comment here"
            rows={4}
            multiline
            name="comment"
            value={formInput.comment || ""}
            variant="outlined"
            // className={classes.commentInput}
            onChange={handleInput}
          />
          {/* <Button type="submit" variant="contained" color="primary">Submit</Button> */}
        </form>
      </DialogContent>

      <DialogActions>
        <Button type="submit" onClick={handleSubmit} color="primary">
          Simpan
        </Button>
      </DialogActions>
    </Dialog>
  );
}

export default ActivityPopup
