import Dialog from '@material-ui/core/Dialog';
import Button from '@material-ui/core/Button';
import DialogTitle from '@material-ui/core/DialogTitle';
import DialogContent from '@material-ui/core/DialogContent';
import DialogActions from '@material-ui/core/DialogActions';
import { makeStyles } from '@material-ui/core/styles';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import TextField from '@material-ui/core/TextField';
import styles from './styles'
import { useFormik } from 'formik';
import * as yup from 'yup';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import { useState } from 'react';

const useStyles = makeStyles(styles);

const validationSchema = yup.object({
  title: yup.string().required(),
  priority: yup.string(),
});

type Props = {
  openDialog: boolean;
  title: string;
  setOpenDialog: Function;
  onRefetch: Function;
  initialValues: {
    // activity_group_id: string;
    title: string;
    priority: string;
  }
}

const TodoPopup: React.FC<Props> = (props) => {
  const classes = useStyles()
  const [selectState, setSelectState] = useState('very-high');

  const formik = useFormik({
    initialValues: {
      ...props.initialValues
    },
    validationSchema,
    onSubmit: (values) => {
      const { title, priority } = values!!

      // handlePostData(activityName)
    },
  })

  const handleClose = () => {
    props.setOpenDialog(false);
  };

  const handleSelectChange = (event) => {
    setSelectState(event.target.value);
  };

  return (
    <Dialog className={classes.dialog} open={props.openDialog} onClose={handleClose} aria-labelledby="form-dialog-title">
      <DialogTitle id="form-dialog-title" className={classes.dialogTitle}>
        "TITLE"

        <IconButton aria-label="close" className={classes.closeButton} onClick={handleClose}>
          <CloseIcon />
        </IconButton>
      </DialogTitle>

      <DialogContent dividers className={classes.dialogContent}>
        <form onSubmit={formik.handleSubmit}>
          <div className={classes.formGroup}>
            <label htmlFor="title">Nama List Item</label>
            <TextField
              rows={4}
              name="title"
              placeholder="Tambahkan nama list item"
              value={formik.values.title}
              variant="outlined"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              autoFocus
              className={
                `${formik.errors.title && formik.touched.title ? classes.formikErrors : null} ${classes.activityNameInput}`
              }
              autoComplete="off"
              required
            />
          </div>

          <div className={classes.formGroup}>
            <label htmlFor="priority">Priority</label>
            <Select
              name="priority"
              id="priority"
              value={selectState}
              onChange={formik.handleChange}
            >
              <MenuItem value="very-high">Very High</MenuItem>
              <MenuItem value='high'>High</MenuItem>
              <MenuItem value='normal'>Medium</MenuItem>
              <MenuItem value='low'>Low</MenuItem>
              <MenuItem value='very-low'>Very Low</MenuItem>
            </Select>
            {/* <TextField
              rows={4}
              name="priority"
              placeholder="Tambahkan nama list item"
              value={formik.values.priority}
              variant="outlined"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              autoFocus
              className={
                `${formik.errors.priority && formik.touched.priority ? classes.formikErrors : null} ${classes.activityNameInput}`
              }
              autoComplete="off"
              required
            /> */}
          </div>
        </form>
      </DialogContent>

      <DialogActions>
        <Button type="submit" onClick={() => formik.handleSubmit()} variant="contained" color="primary">
          Simpan
        </Button>
      </DialogActions>
    </Dialog>
  );
}

export default TodoPopup
