import React, { useEffect, useRef, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { useRouter } from "next/router"
import styles from './styles'
import ActivityHeader from '../../../components/activity-header';
import ActivityPopup from '../../activityPopup';
import { ACTIVITY_GROUPS } from '../../../constant/api';
import { get } from '../../../api/API';
import Grid from '@material-ui/core/Grid';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';

const useStyles = makeStyles(styles)

const DetailActivity = () => {
  const cs = useStyles()
  const router = useRouter()

  const [data, setData] = useState({ title: '' })
  const [loading, setLoading] = useState(false);
  const [addActivityDialog, setAddActivityDialog] = useState(false);
  const [editActivityDialog, setEditActivityDialog] = useState(false);

  const [items, setItems] = useState([
    {
      id: '1',
      name: 'Telur Ayam',
      priority: '1'
    },
    {
      id: '2',
      name: 'Beras 5kg',
      priority: '1'
    },
  ])
  const [checkedState, setCheckedState] = React.useState({});
  const checkedRef = useRef({})

  const fetchDataById = () => {
    const url = ACTIVITY_GROUPS + `/${router.query?.id}`

    const obj = {}

    const onFetch = async () => {
      try {
        const responses = await get(url)
        setData({
          title: responses?.title
        })
        setLoading(false)

      } catch (error) {
        console.error(error)
        setLoading(true)
      }
    }

    onFetch()

  }

  useEffect(fetchDataById, []);

  useEffect(() => {
    Object.keys(items).map(v => {
      checkedRef.current[`checked_${items[v].id}`] = false
    })

    setCheckedState({ ...checkedRef.current })
  }, [])

  const renderChildDialog = () => {
    if (editActivityDialog) return (
      <ActivityPopup
        type="edit"
        title="Edit Activity"
        initialValues={{ activityName: data?.title }}
        openDialog={editActivityDialog}
        setOpenDialog={setEditActivityDialog}
        onRefetch={fetchDataById}
      />
    )

    return <React.Fragment />
  }

  const handleCheckChange = (event) => {
    // setCheckedState({ ...state, [event.target.name]: event.target.checked });
  };

  return (
    <div role="detailActivity">
      {JSON.stringify(checkedState)}
      <ActivityHeader
        title={data?.title || "Loading..."}
        onClick={console.log}
        onUpdate={() => setEditActivityDialog(true)}
        children={renderChildDialog()}
      />

      <Grid container spacing={3} alignItems="stretch">
        <FormControlLabel
          control={
            <Checkbox
              checked={false}
              onChange={handleCheckChange}
              name="checkedF"
            />
          }
          label="Telur Ayam"
        />
      </Grid>
    </div>
  )
}

export default DetailActivity
