import React, { useState, useCallback, useEffect } from "react"
import ActivityCard from "../components/activity-card"
import ActivityHeader from "../components/activity-header"
import Loading from "../components/loading"
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import EmptyItems from "../components/empty-items";
import { get } from '../api/API'
import { ACTIVITY_GROUPS } from '../constant/api'
import { parseDate } from "../utils/utils";
import { useRefetch } from "../hooks/useRefetch";

const useStyles = makeStyles(({}))

const Activity = () => {
  const classes = useStyles()

  const [loading, setLoading] = useState(true)
  const [data, setData] = useState([])
  const { doRefetch, setDoRefetch } = useRefetch()

  const fetchData = () => {
    const url = ACTIVITY_GROUPS + '?email=notfound@gmail.com'

    const onFetch = async () => {
      try {
        const responses = await get(url)
        setData(responses?.data)
        setLoading(false)
        setDoRefetch(true)
       } catch (error) {
        setLoading(true)
       }
    }

    onFetch()
  }

  useEffect(fetchData, [doRefetch]);

  const renderActivityItems = () => {
    if (loading) return <Loading />

    if (!data && !data.length) return (
      <EmptyItems type="activity" />
    )

    return data.map((item, index) => 
      <Grid item xs={12} sm={3} key={`item-${item.id}-${index}`}>
        <ActivityCard
          title={item.title}
          date={parseDate(item.created_at)}
        />
      </Grid>
    )
  }

  return (
    <div role="activity">
      <ActivityHeader />

      <Grid container spacing={3} alignItems="stretch">
        {renderActivityItems()}
      </Grid>
    </div>
  )
}

export default Activity
